using namespace ipxp::process::quic;

static std::optional<ConnectionId> makeConnectionId(const std::optional<std::vector<uint8_t>>& v) {
    return std::nullopt;
}

void TestParser1(const std::vector<uint8_t>& payload_bytes,
 const std::optional<std::vector<uint8_t>>& maybe_cid_bytes,
 uint8_t l4_protocol) {
    const std::byte* start = reinterpret_cast<const std::byte*>(payload_bytes.data());
    std::span<const std::byte> payload_span(start, payload_bytes.size());

    std::optional<ConnectionId> cid = makeConnectionId(maybe_cid_bytes);

    QUICParser parser;
    (void) parser.parse(payload_span, cid, l4_protocol);
}

FUZZ_TEST(FuzzQuicParser, TestParser1);


TEST(FuzzQuicParser, TestParser1Regression) {
  TestParser1(
    fuzztest::ToByteArray(std::string("\336\n\n\n\n\000\013\301\036\036\036\036\001v\260v\266vvvvv", 22)),
    fuzztest::ToByteArray(std::string("\005", 1)),
    17
  );
}



